mapper.select:
/* space.cuongnh2k.rest.account.AccountRepository.getAccount */
SELECT ID, EMAIL, PASSWORD, ACTIVATION_CODE, IS_ACTIVATED
FROM ACCOUNT
WHERE IS_DELETED = 'NO'
  AND EMAIL = #{email}

mapper.insert:
/* space.cuongnh2k.rest.account.AccountRepository.createAccount */
INSERT INTO ACCOUNT (ID, EMAIL, PASSWORD, FIRST_NAME, LAST_NAME, ACTIVATION_CODE)
VALUES (#{id}, #{email}, #{password}, #{firstName}, #{lastName}, #{activationCode});
