mapper.select:
/* space.cuongnh2k.rest.account.AccountRepository.getOneByEmail */
SELECT EMAIL, PASSWORD
FROM ACCOUNT
WHERE IS_DELETED = 'NO'
  AND EMAIL = #{email}

mapper.insert:
/* space.cuongnh2k.rest.account.AccountRepository.createAccount */
INSERT INTO ACCOUNT (ID, EMAIL, PASSWORD, FIRST_NAME, LAST_NAME)
VALUES (#{id}, #{email}, #{password}, #{firstName}, #{lastName});
