mapper.select:
/* space.cuongnh2k.rest.account.AccountRepository.getAccount */ SELECT ID, EMAIL, PASSWORD, ACTIVATION_CODE, IS_ACTIVATED, FIRST_NAME, LAST_NAME, AVATAR_FILE_ID FROM ACCOUNT
IS_DELETED = 'NO'
AND EMAIL = #{email}
AND ID = #{id}

mapper.update:
/* space.cuongnh2k.rest.account.AccountRepository.updateAccount */
UPDATE ACCOUNT

    PASSWORD = #{password},
    FIRST_NAME = #{firstName},
    LAST_NAME = #{lastName},
    AVATAR_FILE_ID = #{avatarFileId},
    IS_ACTIVATED = #{isActivated},


    ID = #{id}
    AND ACTIVATION_CODE = #{activationCode}
    AND IS_ACTIVATED = 'NO'
