mapper.select:
/* space.cuongnh2k.rest.device.DeviceRepository.getDevice */
SELECT
ID,
USER_AGENT,
IS_ACTIVATED,
CREATED_DATE,
UPDATED_DATE
FROM DEVICE

    IS_DELETED = 'NO'
    AND ID = #{id}
    AND ACCOUNT_ID = #{accountId}
    AND ACCESS_TOKEN = #{accessToken}
    AND REFRESH_TOKEN = #{refreshToken}
    AND USER_AGENT = #{userAgent}
    AND IS_ACTIVATED = #{isActivated}

mapper.insert:
/* space.cuongnh2k.rest.device.DeviceRepository.createDevice */
INSERT INTO DEVICE (ID, ACCOUNT_ID, ACCESS_TOKEN, REFRESH_TOKEN, USER_AGENT, ACTIVATION_CODE)
VALUES (#{id}, #{accountId}, #{accessToken}, #{refreshToken}, #{userAgent}, #{activationCode})

mapper.update:
/* space.cuongnh2k.rest.device.DeviceRepository.updateDevice */
UPDATE DEVICE

    ACCESS_TOKEN = #{accessToken},
    REFRESH_TOKEN = #{refreshToken},
    ACTIVATION_CODE = #{activationCodeUseUpdate},
    IS_ACTIVATED = #{isActivated},
    IS_DELETED = #{isDeleted},


    ID = #{id}
    ACCOUNT_ID = #{accountId}
    AND ACTIVATION_CODE = #{activationCodeUseActive}


            #{item}
