mapper.select:
/* space.cuongnh2k.rest.device.DeviceRepository.getDevice */
SELECT
ID,
USER_AGENT,
ACTIVATION_CODE,
IS_ACTIVATED,
CREATED_DATE,
UPDATED_DATE
FROM DEVICE

    IS_DELETED = 'NO'
    AND ID = #{id}
    AND ACCOUNT_ID = #{accountId}
    AND ACCESS_TOKEN = #{accessToken}
    AND REFRESH_TOKEN = #{refreshToken}
    AND USER_AGENT = #{userAgent}
    AND IS_ACTIVATED = #{isActivated}

mapper.update:
/* space.cuongnh2k.rest.device.DeviceRepository.updateDevice */ UPDATE DEVICE
ACCESS_TOKEN = #{accessToken}, REFRESH_TOKEN = #{refreshToken}, ACTIVATION_CODE = #{activationCode}, IS_ACTIVATED = #{isActivated},
IS_DELETED = 'NO' AND ID = #{id} AND IS_ACTIVATED = 'NO'

/* space.cuongnh2k.rest.device.DeviceRepository.deleteDevice */
UPDATE DEVICE

    IS_DELETED = 'YES'


    IS_DELETED = 'NO'
    AND IS_ACTIVATED = 'YES'
    AND ACCOUNT_ID = #{accountId}


            #{item}



        AND USER_AGENT = #{userAgent}
