mapper.select:
/* space.cuongnh2k.rest.file.FileRepository.getFile */
SELECT
ID,
OWNER_ID,
NAME,
CONTENT_TYPE,
SIZE
FROM FILE

    IS_DELETED = 'NO'
    AND ID = #{id}
    AND OWNER_ID = #{ownerId}


            #{item}

mapper.update:
/* space.cuongnh2k.rest.file.FileRepository.deleteFile */
UPDATE FILE

    IS_DELETED = 'YES'


    IS_DELETED = 'NO'
    AND OWNER_ID = #{ownerId}


            #{item}
